components/branding:
{{#if settings.store_logo.image.data}}
  <a class="logo" href="/">
    <img class="{{location}}-logo-image" src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.image.alt}}">
  </a>
{{/if}}
<h1 class="text {{#if settings.store_logo.image.data}}visually-hidden{{/if}}"><a href="/">{{settings.store_logo.title}}</a></h1>

components/navigation-footer:
{{#if navigation}}
  <ul class="footer-menu">
    <li class="footer-menu-header">{{heading}}</li>
    {{#each (limit navigation cap)}}
      <li class="footer-menu-item">
        <a href="{{url}}">{{name}}</a>
        {{#if children}}
          <ul class="footer-menu-tier-secondary">
            {{#each children}}
              <li class="footer-menu-item-secondary">
                <a href="{{url}}">{{name}}</a>
                {{#if children}}
                  <ul class="footer-menu-tier-tertiary">
                    {{#each children}}
                      <li class="footer-menu-item-tertiary">
                        <a href="{{url}}">{{name}}</a>
                      </li>
                    {{/each}}
                  </ul>
                {{/if}}
              </li>
            {{/each}}
          </ul>
        {{/if}}
      </li>
    {{/each}}
    {{#if cap '<' navigation.length}}
      <li class="footer-menu-catchall">
        <a href="{{urls.brands}}">{{lang 'common.footer.all'}} {{heading}}</a>
      </li>
    {{/if}}
    {{#if theme_settings.sitemap}}
      {{#if sitemap_url}}
        <li class="footer-menu-item">
          <a href="{{sitemap_url}}">{{lang 'common.sitemap'}}</a>
        </li>
      {{/if}}
    {{/if}}
  </ul>
{{/if}}

components/navigation-footer-accounts:
{{#any settings.account_creation_enabled settings.show_wishlist settings.gift_certificates_enabled}}
  <ul class="footer-accounts-list">
    {{#if settings.account_creation_enabled}}
      {{#unless customer.id}}
        <li><a href="{{urls.auth.login}}">{{{lang 'common.account.log_in'}}}</a></li>
      {{else}}
        <li><a href="{{urls.account.index}}">{{{lang 'common.account.account'}}}</a></li>
        <li><a href="{{urls.auth.logout}}">{{{lang 'common.account.log_out'}}}</a></li>
      {{/unless}}
    {{/if}}
    {{#if settings.gift_certificates_enabled}}
      <li><a href="{{urls.gift_certificate.purchase}}">{{{lang 'common.gift_certificates'}}}</a></li>
    {{/if}}
    {{#if settings.show_wishlist}}
      <li><a href="{{urls.account.wishlists.all}}">{{{lang 'common.wishlist'}}}</a></li>
    {{/if}}
  </ul>
{{/any}}
