components/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-select-wrapper" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
      <svg class="icon-arrow-down">
        <use xlink:href="#icon-arrow-down"></use>
      </svg>
    </span>
    <select>
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}}>
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/customer-links:
{{#if settings.account_creation_enabled}}
  <div class="customer-links">
    {{#if customer}}
      <a href="{{urls.account.index}}">{{customer.name}}</a>
      <a class="customer-link-logout" href="{{urls.auth.logout}}">{{lang 'common.account.log_out'}}</a>
    {{else}}
      <a href="{{urls.auth.login}}">{{lang 'common.account.log_in'}}</a>
    {{/if}}
  </div>
{{/if}}

components/branding:
{{#if settings.store_logo.image.data}}
  <a class="logo" href="/">
    <img class="{{location}}-logo-image" src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.image.alt}}">
  </a>
{{/if}}
<h1 class="text {{#if settings.store_logo.image.data}}visually-hidden{{/if}}"><a href="/">{{settings.store_logo.title}}</a></h1>

components/navigation-item:
{{#if children}}
  <a href="#" class="submenu-trigger" data-submenu-trigger>{{name}}</a>
  <ul class="dropdown nav-submenu">
    {{#if show_all}}
      <li class="nav-submenu-item">
        <a href="{{url}}">{{lang 'common.all_text'}} {{name}}</a>
      </li>
    {{/if}}
    {{#each children}}
      <li class="nav-submenu-item">
        {{> components/navigation-item}}
      </li>
    {{/each}}
  </ul>
{{else}}
  <a href="{{url}}">{{name}}</a>
{{/if}}

