components/brand/product-listing:
{{#if brand.products}}
  <div class="collection-product-listing {{#if brand.faceted_search_enabled}}with-sidebar{{/if}}">
    <form action="/compare" method="POST" class="form {{#if brand.show_compare}}compare-enabled{{/if}}" data-product-compare>
      <div class="product-grid {{#if brand.faceted_search_enabled}}grid-l-3{{/if}} grid-m-2">
        {{#each brand.products}}
          {{> components/product/product-item this
            show_rating=settings.show_product_ratings
            settings=../theme_settings
            show_compare=../brand.show_compare show_wishlist=settings.show_wishlist}}
        {{/each}}
      </div>
      {{#if brand.show_compare}}
        {{> components/common/compare-tab}}
      {{/if}}
    </form>
  </div>
{{else}}
  <div class="collection-empty">
    <h2 class="search-again">{{lang 'search.empty'}}</h2>
    <form class="faceted-search-form" action="/search.php">
      <input class="faceted-search-input form-input" name="search_query" placeholder="{{lang 'search.placeholder'}}" type="text">
      <button class="faceted-search-submit button button-primary" type="submit">{{lang 'search.submit'}}</button>
    </form>
  </div>
{{/if}}

{{#if brand.products.length}}
  {{> components/common/pagination pagination.brand}}
{{/if}}

components/product/price:
{{!
  Shows a product's price.

  Prices are fairly complicated. Besides having to be able to show the price
  with and without tax, a merchant can also assign a Recommended Retail Price,
  and a Sale Price.

  This means that in extreme cases, we have to show 4 different prices at once.

  When a Sale Price is set we do not have direct access to it--instead it
  overwrites the regular price.

  @param price object

  @param show_tax_labels bool
         If true, show a label saying the price is with or without tax.

  @param show_savings bool
         If true, show a label saying how much is saved from the full price.
}}
{{#if price}}
  {{#or has_options product.options.length}}
    <div class="price {{#all price.without_tax price.with_tax}}multiple-prices{{/all}}">
      {{#if price.without_tax}}
        <span class="price-withouttax" data-product-price-wrapper="without-tax">

          {{#if page_type '!==' 'product'}}
            {{#if price_as_low_as}}
              <span>{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_without_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-product-price>
            {{price.without_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.with_tax}}
              <span class="price-tax-label">{{lang 'product.excluding_tax'}}</span>
            {{/if}}
          {{/if}}

          {{#all
            sale_badges
            price.saved
          }}
            <span class="sale-badge">
              {{lang 'product.sale_badge'}}
            </span>
          {{/all}}
        </span>
      {{/if}}

      {{#if price.with_tax}}
        <span class="price-withtax" data-product-price-wrapper="with-tax">
          {{#if page_type '!==' 'product'}}
            {{#if price_as_low_as}}
              <span>{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_with_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-prodct-price>
            {{price.with_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.without_tax}}
              <span class="price-tax-label">{{lang 'product.including_tax'}}</span>
            {{/if}}
          {{/if}}

          {{#all sale_badges
            price.saved}}
            <span class="sale-badge">
              {{lang 'product.sale_badge'}}
            </span>
          {{/all}}
        </span>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </div>
  {{else}}
    <div class="price {{#all price.without_tax price.with_tax}}multiple-prices{{/all}}">

      {{#if price.without_tax}}
        <span class="price-withouttax" data-product-price-wrapper="without-tax">

          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_without_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-product-price>
            {{price.without_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.with_tax}}
              <span class="price-tax-label">{{lang 'product.excluding_tax'}}</span>
            {{/if}}
          {{/if}}

          {{#all
            sale_badges
            price.saved
          }}
            <span class="sale-badge">
              {{lang 'product.sale_badge'}}
            </span>
          {{/all}}
        </span>
      {{/if}}

      {{#if price.with_tax}}
        <span class="price-withtax" data-product-price-wrapper="with-tax">

          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_with_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-prodct-price>
            {{price.with_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.without_tax}}
              <span class="price-tax-label">{{lang 'product.including_tax'}}</span>
            {{/if}}
          {{/if}}

          {{#all
            sale_badges
            price.saved
          }}
            <span class="sale-badge">
              {{lang 'product.sale_badge'}}
            </span>
          {{/all}}
        </span>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </div>
  {{/or}}
{{/if}}
